// src/components/MarsPhotos.jsx
import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  CircularProgress,
  Grid,
  Card,
  CardHeader,
  CardMedia,
  CardContent,
  CardActionArea
} from '@mui/material';
import { fetchMarsPhotos } from '../api/nasa';

export default function MarsPhotos() {
  const [rover, setRover]     = useState('curiosity');
  const [sol, setSol]         = useState(1000);
  const [photos, setPhotos]   = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError]     = useState(null);

  const loadPhotos = async e => {
    if (e) e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const { photos: fetched } = await fetchMarsPhotos(rover, sol);
      setPhotos(fetched || []);
    } catch (err) {
      setError(err.message || 'Failed to fetch photos');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPhotos();
  }, []);

  // Group photos by camera full_name
  const grouped = photos.reduce((acc, photo) => {
    const cam = photo.camera.full_name;
    if (!acc[cam]) acc[cam] = [];
    acc[cam].push(photo);
    return acc;
  }, {});

  if (loading) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Typography color="error" sx={{ mb: 2 }}>
        {error}
      </Typography>
    );
  }

  return (
    <Box component="section" sx={{ mb: 4 }}>
      {/* Filters */}
      <Box component="form" onSubmit={loadPhotos} sx={{ display: 'flex', gap: 2, mb: 3 }}>
        <TextField
          select
          label="Rover"
          value={rover}
          onChange={e => setRover(e.target.value)}
          SelectProps={{ native: true }}
        >
          {['curiosity','opportunity','spirit'].map(r => (
            <option key={r} value={r}>
              {r.charAt(0).toUpperCase() + r.slice(1)}
            </option>
          ))}
        </TextField>
        <TextField
          label="Sol (Martian day)"
          type="number"
          value={sol}
          onChange={e => setSol(+e.target.value)}
        />
        <Button variant="contained" type="submit">
          Fetch
        </Button>
      </Box>

      {/* Camera Groups */}
      <Grid container spacing={3}>
        {Object.entries(grouped).map(([cameraName, camsPhotos]) => (
          <Grid item xs={12} md={6} key={cameraName}>
            <Card>
              <CardHeader
                title={cameraName}
                titleTypographyProps={{ variant: 'h6' }}
              />
              <CardContent>
                <Grid container spacing={1}>
                  {camsPhotos.map(photo => (
                    <Grid item xs={6} sm={4} key={photo.id}>
                      <CardActionArea
                        href={photo.img_src.replace(/^http:\/\//, 'https://')}
                        target="_blank"
                      >
                        <CardMedia
                          component="img"
                          height="120"
                          image={photo.img_src.replace(/^http:\/\//, 'https://')}
                          alt={cameraName}
                          sx={{ objectFit: 'cover' }}
                        />
                      </CardActionArea>
                    </Grid>
                  ))}
                </Grid>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
