// src/components/MarsPhotos.jsx
import React, { useState, useEffect } from 'react';
import { fetchMarsPhotos } from '../api/nasa';
import {
  Box, TextField, Button, CircularProgress,
  Grid, Card, CardMedia, CardContent, Typography
} from '@mui/material';

export default function MarsPhotos() {
  const [rover, setRover] = useState('curiosity');
  const [sol, setSol] = useState(1000);
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const load = async (evt) => {
    if (evt) evt.preventDefault();
    setLoading(true); setError(null);
    try {
      const res = await fetchMarsPhotos(rover, sol);
      setPhotos(res.photos);
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); }, []);

  return (
    <Box component="section" sx={{ mb: 4 }}>
      <Typography variant="h5" gutterBottom> Mars Rover Photos </Typography>

      <Box component="form" onSubmit={load} sx={{ display: 'flex', gap: 2, mb: 2 }}>
        <TextField
          select
          label="Rover"
          value={rover}
          onChange={e => setRover(e.target.value)}
          SelectProps={{ native: true }}
        >
          {['curiosity','opportunity','spirit'].map(r => (
            <option key={r} value={r}>{r}</option>
          ))}
        </TextField>
        <TextField
          label="Sol (Martian day)"
          type="number"
          value={sol}
          onChange={e => setSol(Number(e.target.value))}
        />
        <Button variant="contained" type="submit" disabled={loading}>
          {loading ? <CircularProgress size={24} /> : 'Fetch'}
        </Button>
      </Box>

      {error && <Typography color="error">{error}</Typography>}

      {loading
        ? <Box sx={{ textAlign: 'center', py: 4 }}><CircularProgress /></Box>
        : <Grid container spacing={2}>
            {photos.map(p => (
              <Grid item xs={12} sm={6} md={4} key={p.id}>
                <Card>
                  <CardMedia
                    component="img"
                    image={p.img_src}
                    alt={p.camera.full_name}
                    sx={{ height: 160, objectFit: 'cover' }}
                  />
                  <CardContent>
                    <Typography variant="subtitle2">{p.camera.full_name}</Typography>
                    <Typography variant="caption" color="textSecondary">{p.earth_date}</Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
      }
    </Box>
  );
}
