// src/App.jsx
import React, { useState, useEffect } from 'react';
import { createTheme, ThemeProvider, CssBaseline, Container, Box } from '@mui/material';
import NavBar from './components/NavBar';
import ApodViewer from './components/ApodViewer';
import MarsPhotos from './components/MarsPhotos';

export default function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [selectedTab, setSelectedTab] = useState('apod');

  const theme = createTheme({ palette: { mode: darkMode ? 'dark' : 'light' } });

  useEffect(() => {
    // nothing else needed—MUI theme handles the rest
  }, [darkMode]);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <NavBar
        selectedTab={selectedTab}
        onSelectTab={setSelectedTab}
        darkMode={darkMode}
        toggleDark={() => setDarkMode(m => !m)}
      />
      <Container maxWidth="md" sx={{ py: 4 }}>
        {selectedTab === 'apod' && <ApodViewer />}
        {selectedTab === 'mars' && <MarsPhotos />}
        {/* Add NeowsList and DataAnalysis similarly */}
      </Container>
    </ThemeProvider>
  );
}
