// src/components/MarsPhotos.jsx
import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  CircularProgress,
  Grid,
  Card,
  CardMedia,
  CardContent
} from '@mui/material';
import { fetchMarsPhotos } from '../api/nasa';

export default function MarsPhotos() {
  const [rover, setRover]     = useState('curiosity');
  const [sol, setSol]         = useState(1000);
  const [photos, setPhotos]   = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError]     = useState(null);

  const loadPhotos = async e => {
    if (e) e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const { photos: fetched } = await fetchMarsPhotos(rover, sol);
      setPhotos(fetched || []);
    } catch (err) {
      setError(err.message || 'Failed to fetch photos');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPhotos();
  }, []);

  // Group photos by camera full_name
  const grouped = photos.reduce((acc, photo) => {
    const cam = photo.camera.full_name;
    if (!acc[cam]) acc[cam] = [];
    acc[cam].push(photo);
    return acc;
  }, {});

  return (
    <Box component="section" sx={{ mb: 4 }}>
      <Typography variant="h5" gutterBottom>
        Mars Rover Photos
      </Typography>

      <Box component="form" onSubmit={loadPhotos} sx={{ display: 'flex', gap: 2, mb: 3 }}>
        <TextField
          select
          label="Rover"
          value={rover}
          onChange={e => setRover(e.target.value)}
          SelectProps={{ native: true }}
        >
          {['curiosity','opportunity','spirit'].map(r => (
            <option key={r} value={r}>
              {r.charAt(0).toUpperCase() + r.slice(1)}
            </option>
          ))}
        </TextField>
        <TextField
          label="Sol (Martian day)"
          type="number"
          value={sol}
          onChange={e => setSol(+e.target.value)}
        />
        <Button variant="contained" type="submit" disabled={loading}>
          {loading ? <CircularProgress size={24} /> : 'Fetch'}
        </Button>
      </Box>

      {error && (
        <Typography color="error" sx={{ mb: 2 }}>
          {error}
        </Typography>
      )}

      {loading ? (
        <Box sx={{ textAlign: 'center', py: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        // Render each camera section
        Object.entries(grouped).map(([cameraName, camsPhotos]) => (
          <Box key={cameraName} sx={{ mb: 5 }}>
            <Typography variant="h6" sx={{ mb: 2 }}>
              {cameraName}
            </Typography>
            <Grid container spacing={2}>
              {camsPhotos.map(photo => (
                <Grid item xs={12} sm={6} md={4} key={photo.id}>
                  <Card>
                    <CardMedia
                      component="img"
                      src={photo.img_src.replace(/^http:\/\//, 'https://')}
                      alt={cameraName}
                      sx={{ height: 160, objectFit: 'cover' }}
                    />
                    <CardContent>
                      <Typography variant="caption" color="textSecondary">
                        {photo.earth_date}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
        ))
      )}
    </Box>
  );
}
