// src/components/MarsPhotos.jsx

import React, { useState, useEffect, useMemo } from 'react';

import {
  Box,
  Typography,
  TextField,
  Button,
  CircularProgress,
  Grid,
  Card,
  CardActionArea,
  CardMedia,
  CardContent,
  IconButton
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { fetchMarsPhotos } from '../api/nasa';

export default function MarsPhotos() {
  const [rover, setRover]             = useState('curiosity');
  const [sol, setSol]                 = useState(1000);
  const [photos, setPhotos]           = useState([]);
  const [loading, setLoading]         = useState(false);
  const [error, setError]             = useState(null);
  const [selectedCamera, setSelectedCamera] = useState(null);

  // Fetch photos
  const loadPhotos = async (e) => {
    if (e) e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const { photos: fetched } = await fetchMarsPhotos(rover, sol);
      setPhotos(fetched || []);
      setSelectedCamera(null); // reset to category view
    } catch (err) {
      setError(err.message || 'Failed to fetch photos');
    } finally {
      setLoading(false);
    }
  };
  useEffect(() => { loadPhotos(); }, []);

  // Group by camera
  const grouped = useMemo(() => {
    return photos.reduce((acc, p) => {
      const cam = p.camera.full_name;
      if (!acc[cam]) acc[cam] = [];
      acc[cam].push(p);
      return acc;
    }, {});
  }, [photos]);

  // Category cards data
  const categories = useMemo(() => {
    return Object.entries(grouped).map(([cameraName, camPhotos]) => ({
      cameraName,
      coverUrl: camPhotos[0]?.img_src.replace(/^http:\/\//, 'https://'),
      count: camPhotos.length,
    }));
  }, [grouped]);

  // Photos to display in gallery
  const galleryPhotos = selectedCamera ? grouped[selectedCamera] : [];

  // Render loading or error
  if (loading) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <CircularProgress />
      </Box>
    );
  }
  if (error) {
    return <Typography color="error">{error}</Typography>;
  }

  return (
    <Box component="section" sx={{ mb: 4 }}>
      <Typography variant="h5" gutterBottom>
        Mars Rover Photos
      </Typography>

      {/* Filters */}
      <Box component="form" onSubmit={loadPhotos} sx={{ display: 'flex', gap: 2, mb: 3, flexWrap: 'wrap' }}>
        <TextField
          select
          label="Rover"
          value={rover}
          onChange={e => setRover(e.target.value)}
          SelectProps={{ native: true }}
          sx={{ minWidth: 140 }}
        >
          {['curiosity','opportunity','spirit'].map(r => (
            <option key={r} value={r}>
              {r.charAt(0).toUpperCase() + r.slice(1)}
            </option>
          ))}
        </TextField>

        <TextField
          label="Sol (Martian day)"
          type="number"
          value={sol}
          onChange={e => setSol(+e.target.value)}
          sx={{ width: 120 }}
        />

        <Button variant="contained" type="submit">
          Fetch
        </Button>
      </Box>

      {/* Category View */}
      {!selectedCamera && (
        <Grid container spacing={2}>
          {categories.map(({ cameraName, coverUrl, count }) => (
            <Grid item xs={12} sm={6} md={4} key={cameraName}>
              <Card>
                <CardActionArea onClick={() => setSelectedCamera(cameraName)}>
                  {coverUrl && (
                    <CardMedia
                      component="img"
                      height="160"
                      image={coverUrl}
                      alt={cameraName}
                      sx={{ objectFit: 'cover' }}
                    />
                  )}
                  <CardContent>
                    <Typography variant="subtitle1">{cameraName}</Typography>
                    <Typography variant="caption" color="textSecondary">
                      {count} photo{count !== 1 && 's'}
                    </Typography>
                  </CardContent>
                </CardActionArea>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

      {/* Gallery View */}
      {selectedCamera && (
        <>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <IconButton onClick={() => setSelectedCamera(null)}>
              <ArrowBackIcon />
            </IconButton>
            <Typography variant="h6" sx={{ ml: 1 }}>
              {selectedCamera} Photos
            </Typography>
          </Box>
          <Grid container spacing={2}>
            {galleryPhotos.map(photo => (
              <Grid item xs={12} sm={6} md={4} key={photo.id}>
                <Card>
                  <CardActionArea
                    href={photo.img_src.replace(/^http:\/\//, 'https://')}
                    target="_blank"
                  >
                    <CardMedia
                      component="img"
                      height="140"
                      image={photo.img_src.replace(/^http:\/\//, 'https://')}
                      alt={selectedCamera}
                    />
                  </CardActionArea>
                </Card>
              </Grid>
            ))}
          </Grid>
        </>
      )}
    </Box>
  );
}
